<template>
  <div class="admin-dashboard">
    <div class="stats-container">
      <el-row :gutter="20">
        <el-col :span="6">
          <el-card class="stat-card">
            <div class="stat-item">
              <el-icon><User /></el-icon>
              <div class="label">用户总数</div>
              <div class="value">{{ stats.userCount }}</div>
            </div>
          </el-card>
        </el-col>
        <el-col :span="6">
          <el-card class="stat-card">
            <div class="stat-item">
              <el-icon><Document /></el-icon>
              <div class="label">题目总数</div>
              <div class="value">{{ stats.problemCount }}</div>
            </div>
          </el-card>
        </el-col>
        <el-col :span="6">
          <el-card class="stat-card">
            <div class="stat-item">
              <el-icon><Folder /></el-icon>
              <div class="label">分类总数</div>
              <div class="value">{{ stats.categoryCount }}</div>
            </div>
          </el-card>
        </el-col>
        <el-col :span="6">
          <el-card class="stat-card">
            <div class="stat-item">
              <el-icon><Bell /></el-icon>
              <div class="label">公告总数</div>
              <div class="value">{{ stats.announcementCount }}</div>
            </div>
          </el-card>
        </el-col>
      </el-row>
    </div>

    <!-- 页面标题 -->
    <div class="page-header">
      <h2 class="page-title">解题统计</h2>
      <el-button type="primary" @click="refreshRankings">
        <el-icon><Refresh /></el-icon>
        刷新
      </el-button>
    </div>

    <!-- 排行榜内容 -->
    <div v-loading="loading" class="leaderboard-content">

      <!-- 排行榜表格 -->
      <el-card class="rank-table">
        <el-table :data="rankings" stripe>
          <el-table-column label="排名" width="100" align="center">
            <template #default="{ $index, row }">
              <div class="rank">
                <el-tag :type="getRankType(row.rank)" effect="dark">
                  {{ row.rank }}
                </el-tag>
              </div>
            </template>
          </el-table-column>

          <el-table-column label="用户" min-width="200">
            <template #default="{ row }">
              <div class="user-info">
                <el-avatar :size="32" :src="'data:image/jpeg;base64,' + (row.avatar || defaultAvatar)" />
                <span class="username">{{ row.username }}</span>
              </div>
            </template>
          </el-table-column>

          <el-table-column prop="score" label="分数" width="120" align="center">
            <template #default="{ row }">
              <span class="score">{{ row.score }}</span>
            </template>
          </el-table-column>

          <el-table-column label="解题统计" align="center">
            <template #default="{ row }">
              <div class="solve-stats">
                <el-tooltip content="总解题数">
                  <el-tag type="info" effect="plain">
                    {{ row.solvedCount }}
                  </el-tag>
                </el-tooltip>
              </div>
            </template>
          </el-table-column>
        </el-table>

        <!-- 分页器 -->
        <div class="pagination-container">
          <el-pagination
            v-model:current-page="currentPage"
            v-model:page-size="pageSize"
            :total="total"
            :page-sizes="[10, 20, 50]"
            layout="total, sizes, prev, pager, next"
            @size-change="handleSizeChange"
            @current-change="handleCurrentChange"
          />
        </div>
      </el-card>
    </div>
  </div>
</template>

<script setup>
import { ref, onMounted } from 'vue'
import { getRankingList } from '@/api/ranking'
import { getDashboardStats } from '@/api/admin';
import { User, Document, Folder, Bell, Medal, Trophy } from '@element-plus/icons-vue'
import { ElMessage } from 'element-plus'

const stats = ref({
  userCount: 0,
  problemCount: 0,
  categoryCount: 0,
  announcementCount: 0,
})

const topThree = ref([])
const rankings = ref([])
const total = ref(0)
const loading = ref(false)
const currentPage = ref(1)
const pageSize = ref(10)
const defaultAvatar = ''

const fetchDashboardStats = async () => {
  try {
    const response = await getDashboardStats()
    if (response.code === 200) {
      stats.value = response.data
    } else {
      console.error('Failed to fetch dashboard stats')
    }
  } catch (error) {
    console.error('Error fetching dashboard stats:', error)
  }
}

const fetchRankings = async () => {
  loading.value = true
  try {
    const response = await getRankingList(currentPage.value, pageSize.value)
    if (response.code === 200) {
      if (currentPage.value === 1) {
        topThree.value = response.data.records.slice(0, 3)
      }
      rankings.value = response.data.records
      total.value = response.data.total
    } else {
      ElMessage.error('获取排行榜失败')
    }
  } catch (error) {
    console.error('获取排行榜失败:', error)
    ElMessage.error('获取排行榜失败')
  } finally {
    loading.value = false
  }
}

const refreshRankings = () => {
  fetchRankings()
}

const handleSizeChange = (val) => {
  pageSize.value = val
  fetchRankings()
}

const handleCurrentChange = (val) => {
  currentPage.value = val
  fetchRankings()
}

const getRankType = (rank) => {
  if (rank === 1) return 'danger'
  if (rank === 2) return 'warning'
  if (rank === 3) return 'success'
  return ''
}

onMounted(() => {
  fetchDashboardStats()
  fetchRankings()
})
</script>

